import styled from 'styled-components';

export const CardHeader = styled.h1
`
    display: flex;
    flex-direction: column;
    line-height: 1px;
    background-color: rgba(10, 54, 198, 0.38);
    border: 2px black solid;
    width: 40%;
    margin-bottom: 25px;
    margin-right: 45px;
    padding: 30px;
    border-radius: 10px;

`;

export const CardBody = styled.h2 `
    font-size: 25px;
    background: Red;
`
